var Careless = require('careless');

var background = require('./background');

function wrap(elements) {
  return elements.map(el => <tr><td>{el}</td></tr>);
}

module.exports = function OneCol(props, context, res) {

  return (
    <table border="0" cellpadding="0" cellspacing="0" width="100%">
      <tr>
        <td bgcolor={background(props.pos)} align="center" style="padding: 70px 15px 70px 15px;" class="section-padding">
          <table border="0" cellpadding="0" cellspacing="0" width="500" class="responsive-table">
            <tr>
              <td>
                <table width="100%" border="0" cellspacing="0" cellpadding="0">
                  {wrap(props.children)}
                </table>
              </td>
            </tr>
          </table>
        </td>
      </tr>
    </table>
  )
};

var Hero = function(props, context, res) {

  var img = props.img || res(__dirname, "img/responsive-email.jpg");

  return (
    <table width="100%" border="0" cellspacing="0" cellpadding="0">
      <tbody>
        <tr>
          <td class="padding-copy">
            <table width="100%" border="0" cellspacing="0" cellpadding="0">
              <tr>
                <td>
                  <a href={props.href} target="_blank"><img src={img} width="500" height="200" border="0" alt={props.alt} style="display: block; padding: 0; color: #666666; text-decoration: none; font-family: Helvetica, arial, sans-serif; font-size: 16px; width: 500px; height: 200px;" class="img-max" /></a>
                </td>
              </tr>
            </table>
          </td>
        </tr>
      </tbody>
    </table>
  )
};
module.exports.Hero = Hero;

var Content = function(props, context, res) {
  return (
    <table width="100%" border="0" cellspacing="0" cellpadding="0">
      <tr>
        <td align="center" style="font-size: 25px; font-family: Helvetica, Arial, sans-serif; color: #333333; padding-top: 30px;" class="padding-copy">{props.title}</td>
      </tr>
      <tr>
        <td align="center" style="padding: 20px 0 0 0; font-size: 16px; line-height: 25px; font-family: Helvetica, Arial, sans-serif; color: #666666;" class="padding-copy">{props.children}</td>
      </tr>
    </table>
  );
};
module.exports.Content = Content;
